import React, { useState } from 'react';
import axios from 'axios';
import '../styles.css';

function PredictionPanel() {
  const [game, setGame] = useState('');
  const [team, setTeam] = useState('');
  const [score, setScore] = useState('');

  const handlePrediction = async () => {
    if (!game || !team || !score) {
      alert("Please complete all fields!");
      return;
    }

    try {
      await axios.post('http://localhost:5000/predict', { game, team, score });
      alert("Prediction Submitted!");
    } catch (error) {
      console.error('Error submitting prediction:', error);
    }
  };

  return (
    <div className="prediction-container">
      <h2>ðŸŽ¯ Make a Prediction</h2>
      <select onChange={(e) => setGame(e.target.value)}>
        <option value="">Select Game</option>
        <option value="Rutgers vs Michigan">Rutgers vs Michigan</option>
      </select>
      <div className="radio-group">
        <label>
          <input type="radio" value="Rutgers" name="team" onChange={(e) => setTeam(e.target.value)} /> Rutgers
        </label>
        <label>
          <input type="radio" value="Michigan" name="team" onChange={(e) => setTeam(e.target.value)} /> Michigan
        </label>
      </div>
      <input type="number" placeholder="Predicted Score" onChange={(e) => setScore(e.target.value)} />
      <button onClick={handlePrediction}>Submit Prediction</button>
    </div>
  );
}

export default PredictionPanel;
