import React, { useState } from 'react';
import axios from 'axios';
import { Card, CardContent, Typography, TextField, Button, RadioGroup, FormControlLabel, Radio } from '@mui/material';
import { motion } from 'framer-motion';

function PredictionPanel() {
  const [game, setGame] = useState('');
  const [team, setTeam] = useState('');
  const [score, setScore] = useState('');

  const handlePrediction = async () => {
    if (!game || !team || !score) {
      alert("Please complete all fields!");
      return;
    }

    try {
      await axios.post('http://localhost:5000/predict', { game, team, score });
      alert("Prediction Submitted!");
    } catch (error) {
      console.error('Error submitting prediction:', error);
    }
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
      <Card>
        <CardContent>
          <Typography variant="h4" gutterBottom>ðŸŽ¯ Make a Prediction</Typography>
          <TextField fullWidth label="Game" variant="outlined" onChange={(e) => setGame(e.target.value)} />
          <RadioGroup row onChange={(e) => setTeam(e.target.value)}>
            <FormControlLabel value="Rutgers" control={<Radio />} label="Rutgers" />
            <FormControlLabel value="Michigan" control={<Radio />} label="Michigan" />
          </RadioGroup>
          <TextField fullWidth type="number" label="Predicted Score" variant="outlined" onChange={(e) => setScore(e.target.value)} />
          <Button variant="contained" color="primary" fullWidth onClick={handlePrediction} style={{ marginTop: '10px' }}>Submit Prediction</Button>
        </CardContent>
      </Card>
    </motion.div>
  );
}

export default PredictionPanel;
