import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Card, CardContent, Typography, Table, TableHead, TableBody, TableRow, TableCell } from '@mui/material';
import { motion } from 'framer-motion';

function Leaderboard() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/leaderboard')
      .then(response => setUsers(response.data))
      .catch(error => console.error('Error fetching leaderboard:', error));
  }, []);

  const data = {
    labels: users.map(user => user.username),
    datasets: [{
      label: 'Points',
      data: users.map(user => user.points),
      backgroundColor: 'rgba(54, 162, 235, 0.5)',
    }]
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
      <Card>
        <CardContent>
          <Typography variant="h4" gutterBottom>üèÜ Leaderboard</Typography>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Rank</TableCell>
                <TableCell>Username</TableCell>
                <TableCell>Points</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {users.length > 0 ? (
                users.map((user, index) => (
                  <TableRow key={index}>
                    <TableCell>{index + 1}</TableCell>
                    <TableCell>{user.username}</TableCell>
                    <TableCell>{user.points}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={3}>Loading...</TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
          <Bar data={data} />
        </CardContent>
      </Card>
    </motion.div>
  );
}

export default Leaderboard;
